import React from 'react'
import { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios'

import conf from './config'

export default function Comic() {

  let { id } = useParams()
  let navigate = useNavigate();

  const [data, setdata] = useState(-1)

  const getComicStrip = (id) => {
    if (id) {
      axios.get(conf.baseURL + `${id}`)
        .then(res => setdata(res.data))
        .catch(err => console.log(err))
    }
    else {
      axios.get(conf.baseURL)
        .then(res => setdata(res.data))
        .catch(err => console.log(err))
    }
  }

  const getNextComicStrip = () => {
    navigate(`${id + 1}`);
  }

  useEffect(() => {
    if (data <= -1) {
      getComicStrip(id)
    }

  }, [id])


  return (
    <div className="v-container">
      <div className="center">
        <div className="title">
          <p>{data['safe_title']}</p>
        </div>
        <div className="box">
          <div className="boxinner"> 344 ðŸ‘€ </div>
          <div className='img-container'>
            <img src={data['img']} className="image"></img>
          </div>
        </div>




      </div>
      <div className="h-container">
        <div>
          <button className="btn" onClick={() => getNextComicStrip()}> prev </button>
        </div>
        <div>
          <button className="btn" onClick={() => getNextComicStrip()}>random</button>
        </div>
        <div>
          <button className="btn" onClick={() => getNextComicStrip()}> next</button>
        </div>
      </div>

    </div >
  )
}
